    public List<IssueEvidenceEventResponse> getIssueEvidenceEvents(
            TransactionReceipt transactionReceipt) {
        List<EventValuesWithLog> valueList = extractEventParametersWithLog(ISSUEEVIDENCE_EVENT, transactionReceipt);
        ArrayList<IssueEvidenceEventResponse> responses = new ArrayList<IssueEvidenceEventResponse>(valueList.size());
        for (EventValuesWithLog eventValues : valueList) {
            IssueEvidenceEventResponse typedResponse = new IssueEvidenceEventResponse();
            typedResponse.log = eventValues.getLog();
            typedResponse.to = (String) eventValues.getIndexedValues().get(0).getValue();
            typedResponse.value = (BigInteger) eventValues.getNonIndexedValues().get(0).getValue();
            typedResponse.evidence = (String) eventValues.getNonIndexedValues().get(1).getValue();
            responses.add(typedResponse);
        }
        return responses;
    }

